sys_relationship.query_with:
(function refineQuery(current, parent) {

	var sourceUnit = parent.sys_id;
	var abilities = "";
	//Get wargear records
	var gr = new GlideRecord("x_903540_40k_arm_0_ability_details");
	gr.addQuery("datasheet_id",sourceUnit);
	gr.query();
	//Add wargear records for query
	while (gr.next()) {
		abilities += gr.ability_id +",";
	}
	//Query the profiles
	current.addQuery("sys_id","IN",abilities);

	// Add your code here, such as current.addQuery(field, value);

})(current, parent);
